mapper.update:
update plans
set plan_name = #{planName}
where plan_id = #{planId}

update plans
set
    start_date = #{startDate},
    end_date = #{endDate}
where plan_id = #{planId}

update plans
set plan_state = #{planState}
where plan_id = #{planId}

mapper.select:
select count(*)
from members
where member_email = #{memberEmail}
